import React, { useState, useEffect } from "react";
import TaskDescriptionComponent from "./TaskDescriptionComponent";

function TaskListComponent(props) {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      const token = props.userID;
      const response = await fetch("http://localhost:8000/api/tasks/getTasks", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ "userID": props.userID })
      });
      const data = await response.json();
      setTasks(data);
    };
    fetchTasks();
  }, [props.userID]);

  const handleTaskClick = (taskId) => {
    setSelectedTask(taskId);
  };

  return (
    <div>
      <h2>Task List</h2>
      {/* {props.userID && <p>Your userID: {props.userID}</p>} */}
      <ul>
        {tasks.map((task) => (
          <li key={task.ID} onClick={() => handleTaskClick(task.ID)}>
            <button><div>&nbsp;&nbsp;&nbsp;&nbsp;{task.Task_title}&nbsp;&nbsp;&nbsp;&nbsp;
            {new Date(task.Create_date).toLocaleDateString()}</div></button>
          </li>
        ))}
      </ul>
      {selectedTask && <TaskDescriptionComponent userID={props.userID} taskID={selectedTask} />}
    </div>
  );
}

export default TaskListComponent;
