import React, { useState, useEffect } from "react";

function EditTaskComponent(props) {
  const [taskDetails, setTaskDetails] = useState(null);
  const [editedTask, setEditedTask] = useState({ Task_title: "", Task_description: "" });

  useEffect(() => {
    const fetchTaskDetails = async () => {
      const token = props.userID;
      const response = await fetch("http://localhost:8000/api/tasks/getTask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({ userID: props.userID, taskID: props.taskID }),
      });
      const data = await response.json();
      setTaskDetails(data);
      setEditedTask({ Task_title: data.Task_title, Task_description: data.Task_description });
    };
    fetchTaskDetails();
  }, [props.userID, props.taskID]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditedTask((prevTask) => ({
      ...prevTask,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    const token = props.userID;
    const response = await fetch("http://localhost:8000/api/tasks/putTask", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        userID: props.userID,
        taskID: props.taskID,
        Task_title: editedTask.Task_title,
        Task_description: editedTask.Task_description,
      }),
    });
    props.onSave();
  };

  return (
    <div className="formDiv">
      <h2>Edit Task</h2>
      {taskDetails ? (
        <form>
          <div>
            <label htmlFor="Task_title">Task Title: </label>
            <input
              type="text"
              id="Task_title"
              name="Task_title"
              value={editedTask.Task_title}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label htmlFor="Task_description">Task Description: </label>
            <textarea
              id="Task_description"
              name="Task_description"
              value={editedTask.Task_description}
              onChange={handleInputChange}
            />
          </div>
          <button type="button" onClick={handleSave}>
            Save Task
          </button>
        </form>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}

export default EditTaskComponent;
