import React, { useState, useEffect } from "react";

function EditTaskComponent(props) {
  const [taskDetails, setTaskDetails] = useState(null);
  const [taskTitle, setTaskTitle] = useState("");
  const [taskDescription, setTaskDescription] = useState("");

  useEffect(() => {
    const fetchTaskDetails = async () => {
      const token = props.userID;
      const response = await fetch("http://localhost:8000/api/tasks/getTask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ "userID": props.userID, "taskID": props.taskID })
      });
      const data = await response.json();
      setTaskDetails(data);
      setTaskTitle(data.Task_title);
      setTaskDescription(data.Task_description);
    };
    fetchTaskDetails();
  }, [props.userID, props.taskID]);

  const handleTaskTitleChange = (event) => {
    setTaskTitle(event.target.value);
  };

  const handleTaskDescriptionChange = (event) => {
    setTaskDescription(event.target.value);
  };

  const handleSaveTask = async () => {
    const token = props.userID;
    const response = await fetch("http://localhost:8000/api/tasks/putTask", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      },
      body: JSON.stringify({ "userID": props.userID, "taskID": props.taskID, "Task_title": taskTitle, "Task_description": taskDescription })
    });
    props.onClose();
  }

  if (!taskDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div className='formDiv'>
      <h2>Edit Task</h2>
      <form>
        <div>
          <label htmlFor="taskTitle">Task Title:</label>
          <input type="text" id="taskTitle" value={taskTitle} onChange={handleTaskTitleChange} />
        </div>
        <div>
          <label htmlFor="taskDescription">Task Description:</label>
          <textarea id="taskDescription" value={taskDescription} onChange={handleTaskDescriptionChange} />
        </div>
        <button type="button" onClick={handleSaveTask}>Save Task</button>
        <button type="button" onClick={props.onClose}>Cancel</button>
      </form>
    </div>
  );
}

export default EditTaskComponent;
