import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(null);
  const [jwt, setJwt] = useState(null);

  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    try {
      const response = await axios.post('http://localhost:8000/api/users/login', {
        username,
        password,
      });
      const { ID } = response.data;
      if (ID > 0) {
        setJwt(ID);
        // Redirect to home page
        // window.location.href = '/';
        navigate('/', { state: { userID: response.data.ID } });
      } else {
        setError('Invalid username or password');
      }
    } catch (error) {
    //   console.error(error);
    //   setError('Failed to log in');
    }
  };

  const handleTogglePassword = () => setShowPassword(!showPassword);

  return (
    <div>
        <nav>
        <ul>
          <li><Link to='/signup'>Sign Up</Link></li>
        </ul>
      </nav>
      <h2>Login</h2>
      <form onSubmit={handleSubmit} className='formDiv'>
        <div>
          <label>
            Username:
            <input type="text" value={username} onChange={(event) => setUsername(event.target.value)} />
          </label>
        </div>
        <div>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css" integrity="sha512-ZqNE3cEo3PZ61HnAA+vbbzkn+d2hdJbAn1v94X/s5oLR50Fh5l5p5xC4Zz1e4G4SKICcQWe/1KBC5C5cp5S5Mw==" crossorigin="anonymous" referrerpolicy="no-referrer" />

          <label>
            Password:
            <div style={{position: 'relative'}}>
                <input type={showPassword ? 'text' : 'password'} value={password} onChange={(event) => setPassword(event.target.value)} />
                <button type="button" style={{position: 'absolute', right: '10px', top: '0%', transform: 'translateY(-50%)'}} onClick={handleTogglePassword}>{showPassword ? 'Hide' : 'Show'}</button>
            </div>
          </label>
          
        </div>
        <button type="submit">Log in</button>
      </form>
      {error && <div>{error}</div>}
      {jwt && <div>Logged in with JWT: {jwt}</div>}
    </div>
  );
}

export default LoginForm;
